int led = 11;  // led를 D11핀에 연결한 것을 선언
int sensor = 12;
 void setup() {
  pinMode(11, OUTPUT);
  pinMode(sensor, INPUT);
}
 void loop() {
  // put your main code here, to run repeatedly:
  if(digitalRead(sensor == HIGH)) //센서 미작동시
  {
    digitalWrite(11, LOW);  //LED도 미작동
  }
  else //센서 작동시
  {
    digitalWrite(11, HIGH); //LED도 작동
  }
}

