}

void loop() {

  Time t = rtc.time();
  if(t.min%30==0 && t.sec==0)
  {
       digitalWrite(A1A, HIGH);
    digitalWrite(B1A, HIGH);
       delay(40000);
    digitalWrite(A2B, LOW);
    digitalWrite(B2B, LOW);
    delay(1000);

  }
}

