using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using System.IO.Ports;

public class PlayerController : MonoBehaviour
{
    public Enum.PortNumber portNumber;
    public int baudRate = 9600;
    private SerialPort serialPort;

    void Start()  // 게임이 처음 시작될 때 1회 실행
    {
        serialPort = new SerialPort(portNumber.ToString(), baudRate, Parity.None, 8, StopBits.One);
        serialPort.Open();
    }

    void Update()

    {   //키보드로 물체 움직이기
        if (serialPort.IsOpen == false)
        {
            return;
        }

        else
        {
            float moveZ = Input.GetAxis("Vertical");

            float moveX = Input.GetAxis("Horizontal");

            transform.Translate(new Vector3(moveX, 0f, moveZ).normalized * 0.01f);
        }
    }

    public void OnTriggerEnter(Collider other)  //다른 물체와 충돌했을 때
    {
        Debug.Log("충돌");
        serialPort.WriteLine("1");  //S라는 데이터 출력
            }

}



