#include <Wire.h>
#include <math.h>

#define pinHead 3
#define LED 5
#define Buzzer 6

boolean checkFalling = false;
long ax = 0, ay = 0, az = 0;
long gx = 0, gy = 0, gz = 0;
long Asvm = 50000;
long Gsvm = 0;

int readPIn = 0;

void setup() {
  // put your setup code here, to run once:
  Wire.begin();         // I2C 통신 활성화 함수
  Serial.begin(9600);   // Serial 통신속도를 9600으로 설정 함수

  Wire.beginTransmission(0x68);   // 마스터에서 전송을 시작하기 위해 슬레이브의 주소 값을 지정
  Wire.write(0x6b);               // 통신을 통해 설정한 값을 전송
  Wire.write(0x0);                
  Wire.endTransmission();         // 전송이 완료됨을 선언

  pinMode(pinHead, INPUT);        // pinHead (3번 I/O Pin)을 INPUT으로 선언
  pinMode(LED, OUTPUT);
  pinMode(Buzzer, OUTPUT);

  digitalWrite(LED, LOW);         // LED에 5V를 차단
  digitalWrite(Buzzer, LOW);      // Buzzer에 5V 차단
  checkFalling = false;           // 변수 checkFalling을 false로 선언
}

void loop() {
  // put your main code here, to run repeatedly:
  getMPUData(); // MPU 에서 가속도 값 수집

  readPIn = digitalRead(pinHead); // pin 연결 확인
  if (readPIn == 1) {
    checkFalling = false;
  }
    long falling_thresh = 3000; // 추락감지 가속도 임계값
  if (Asvm < falling_thresh) {
    //추락
    Serial.println("falling!!!!!");
    checkFalling = true; 
  }

  if (checkFalling == true) { // 추락이 감지되었을때 LED, 부저 동작
    digitalWrite(Buzzer, HIGH);   // Buzeer 5V를 흘려주면 소리가 남
    delay(400);
    digitalWrite(Buzzer, LOW);    // Buzeer 5V를 차단하면 소리가 꺼짐
    delay(400);  
        digitalWrite(LED, HIGH);
    delay(250);     
    digitalWrite(LED, LOW);
    delay(250);
  } else if (checkFalling == false) { // 추락이 아닐때 부저 비동작
    digitalWrite(LED, LOW);           
    digitalWrite(Buzzer, LOW);
  }
}

