using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using UnityEngine.UI;
using System.IO.Ports;

public class GyroscopeScript : MonoBehaviour
{
    public Enum.PortNumber portNumber;
    public int baudRate = 9600;
    public Text outputText;

    private SerialPort serialPort;

    public bool IsInit { get; set; }
    public float AngleX { get; set; }
    public float AngleY { get; set; }
    public float AngleZ { get; set; }

    GyroscopeScript()
    {
        IsInit = false;
    }

    ~GyroscopeScript()
    {
        serialPort.Close();
    }

    void Start()
    {
        serialPort = new SerialPort(portNumber.ToString(), baudRate, Parity.None, 8, StopBits.One);
        serialPort.Open();
        IsInit = false;
    }

    void Update()
    {
        if (serialPort.IsOpen == false)
            return;

        string serialData = serialPort.ReadLine();

        /* Debug */
        if (outputText != null)
        {
            outputText.text = "ReadLine() : ";
            outputText.text += serialData;
        }

        Debug.Log(serialData);

        /* DataSet */
        string[] splitStr = serialData.Split(new char[] { ',' }, System.StringSplitOptions.RemoveEmptyEntries);
        if (splitStr.Length != 3)
            return;

        AngleX = -float.Parse(splitStr[1]);
        AngleY = -float.Parse(splitStr[2]);
        AngleZ = float.Parse(splitStr[0]);

        if (IsInit == false)
            IsInit = true;
    }
}

