//초음파센서로 거리에 따라서 LED 제어하기
int trig = 2, echo = 3;
int rLED = 13,bLED = 7;
int sw1 = 12; int b1 = 10;
void setup() {
  //시리얼 통신 설정
  Serial.begin(9600);
  //핀모드 설정
  pinMode(b1, OUTPUT);
  pinMode(sw1, INPUT_PULLUP);//sw를 인풋 풀업으로하여 스위치가 열려있다면 HIGH 스위치가 닫혀 있다면 LOW
  pinMode(trig, OUTPUT);
  pinMode(echo, INPUT);
  pinMode(rLED, OUTPUT);
  pinMode(bLED, OUTPUT);
}

void loop() {
  if(digitalRead(sw1) == LOW)// 스위치가 눌리면
    digitalWrite(b1, HIGH); // 부저 ON
  else
    digitalWrite(b1, LOW);  //부저 OFF
  float duration, distance;
  //초음파 발사 trig
  digitalWrite(trig, HIGH);
  delay(200);
  digitalWrite(trig, LOW);

  //초음파가 사물 맞고 돌아온 시간 저장
  duration = pulseIn(echo, HIGH);

  //시간을 거리로 cm단위 환산
  distance = ((float)(duration * 340) / 10000) / 2;

  //시리얼 모니터에 거리 출력
  Serial.print("거리 : ");
  Serial.print(distance);
  Serial.println("cm");

  //거리에 따라 LED 제어하기
  if(distance <= 20){
    digitalWrite(bLED, HIGH);
    digitalWrite(rLED, LOW);
  }else if(distance <= 400){
    digitalWrite(bLED, LOW);
    digitalWrite(rLED, HIGH);
  }else{
    digitalWrite(bLED, LOW);
    digitalWrite(rLED, LOW);
  }
}

