/*
  초음파 센서를 이용한 키 재기 입니다.
  초음파 센서의 특성상 아주 정확한 거리측정은 불가하고, 정확한 수치에 근접할 수 있도록 코딩 하였습니다.

  해당 코드는
  0.1초에 한번씩 초음파 센서로 거리값을 10번 구한 뒤,
  10번중 최대값과, 최소값을 뺀 나머지 8개 거리의 평균을 구합니다.

  테스트해본 초음파 센서의 경우 거리가 커질 수록 오차가 심해 정확한 수치를 재는것이 어렵습니다.
  더 좋은 초음파 센서를 사용하면 좋아지지 않을가 생각합니다.
*/
#include <TM1637Display.h>
//TM1637 사용하기 위한 라이브러리는 따로 설치해 주셔야 합니다.
//스케치 >> 라이브러리 포함하기 >> 라이브러리 관리 >> TM1637로 검색 후 Install 해주세요.

int trigPin = 5; //센서 Trig 핀
int echoPin = 6; //센서 Echo 핀

int button = 3;  //시작 종료 버튼
// Pin 2 - > CLK
// Pin 4 - > DIO
TM1637Display dsp(2, 4);

//첫번째 평균값을 구하기 위한 변수
double avg[10];  // 10번중 최대,최소값을 뺀 나머지 7개값의 평균을 구할 배열 >> 10번보다 더 많은 값으로 체크하고자 하는경우 숫자변경 가능합니다. 3보다는 큰 숫자를 사용해야 합니다.
int avgSumCnt = sizeof(avg) / sizeof(double);
int avgCnt = 0;
bool isDistanceStat = true;

//버튼을 한번 눌러도 여러번 눌린 효과 발생 방지위한 코드 변수
int btnClkWait = 0;

//초음파 센서 튀어나온 거리만큼 더해준다.
double addCm = 0.4;
//가지고 있는 초음파 센서마다 약간의 오차가 있음
//내가 가진건 10cm 당 약 0.38cm 의 오차가 있어 이 숫자 만큼 더해주었다.
//가지고 있는 초음파 센서의 10Cm 당 오차를 계산해 아래 변수의 숫자를 바꿔준다.
double Per10CmAddDistance = 0.38;

void setup() {
  // put your setup code here, to run once:
  pinMode(trigPin, OUTPUT);
  pinMode(echoPin, INPUT);
  //버튼제어
  pinMode(button, INPUT_PULLUP);
  attachInterrupt(1, chkbutton, CHANGE);
  dsp.setBrightness(7, true);  //밝기값
  Serial.begin(9600);
}

void loop() {
  if (isDistanceStat) {
    double getDistance = getCm();

    if (getDistance > 0) {
      getDistance = getDistance + addCm;
      int Distance = getDistance + ((getDistance / 10) * Per10CmAddDistance);
      dsp.showNumberDec(Distance, true);  //시간표시
    }
  }
  //버튼 2중처리 안되기 위한 방어처리
  if (btnClkWait > 0) {
    btnClkWait++;
  }
  //10번이니 0.1초 * 10 >> 1초동안 버튼 2번 누르지 않도록 함.
  if (btnClkWait > 10) {
    btnClkWait = 0;
  }
   delay(100);
}

//버튼 클릭 이벤트
void chkbutton() {
  if (btnClkWait < 1) {
    btnClkWait = 1;
    isDistanceStat = !isDistanceStat;
  }
}
//초음파 센서로 거리 구하는 함수
//10번의 값을 배열에 담아 그중 최대값, 최소값을 뺀 나머지 값의 평균을 리턴해줍니다.
double getCm() {
  unsigned long pulseinTime;                                        //unsigned : 음수를 사용하지 않고 양수만 사용(약 42억)
  double mm;

  digitalWrite(trigPin, HIGH);                                      //센서에 Trig 신호 입력
  delayMicroseconds(100);                                          //1000us 정도 유지(최소 10us(마이크로초)이상 HIGH 상태 출력
  digitalWrite(trigPin, LOW);

  pulseinTime = pulseIn(echoPin, HIGH);                             //초음파를 수신할 때까지 시간을 측정 / 아두이노에서 제공하는 함수
  mm = (pulseinTime / 2) / 29.4;                                    //거리(cm)로 변환 공식 / 거리(cm)=측정시간/29.4/2
  //거리 = 속도 * 시간

  //Serial.print("mm - ");
  //Serial.println(mm);

  avg[avgCnt] = mm;
  avgCnt++;

  double avgvl = 0;
  if (avgCnt >= avgSumCnt) {
    avgCnt = 0;
    double sumvl;  //거리 합계, 평균값
    double maxvl, minvl;  //순서대로 최대값, 최소값

    /*
        Serial.print("avg1 - ");
        Serial.print(avg[0]);
        Serial.print(", avg2 - ");
        Serial.print(avg[1]);
        Serial.print(", avg3 - ");
        Serial.print(avg[2]);
        Serial.print(", avg4 - ");
        Serial.print(avg[3]);
        Serial.print(", avg5 - ");
        Serial.print(avg[4]);
    */
    //Serial.print(", avg6 - ");
    //Serial.print(avg[5]);
    //Serial.print(", avg7 - ");
    //Serial.println(avg[6]);

    maxvl = max_arr(avg, avgSumCnt);  //배열의 최대값
    minvl = min_arr(avg, avgSumCnt);  //배열의 최소값
    /*
      Serial.print("avgSumCnt - ");
      Serial.print(avgSumCnt);
      Serial.print(",    maxvl - ");
      Serial.print(maxvl);
      Serial.print("     minvl - ");
      Serial.println(minvl);
    */
    sumvl = 0;
    avgvl = 0;
    for (int i = 0; i < avgSumCnt; i++) {  //합계 구하기
      sumvl += avg[i];
    }
    /*
        Serial.print("sum - ");
        Serial.println(sumvl);
        Serial.print("sum2 - ");
        Serial.println(sumvl - (maxvl + minvl));
    */
    avgvl = (sumvl - (maxvl + minvl)) / (avgSumCnt - 2);   //최대, 최소값 뺀 나머지값의 평균

    //   Serial.print("avgvl - ");
    //   Serial.println(avgvl);
    return avgvl;
  } else {
    return 0;
  }

}
//배열의 최대값 구하는 함수
double max_arr(double arr[], int arrSize) {
  double maxval;

  maxval = arr[0];
  for (int i = 0; i < arrSize; i++) {
    maxval = max(maxval, arr[i]);
  }
  return maxval;
}
//배열의 최소값 구하는 함수
double min_arr(double arr[], int arrSize) {
  double minval;

  minval = arr[0];
  for (int i = 0; i < arrSize; i++) {
    minval = min(minval, arr[i]);
  }
  return minval;
}

