#include <Wire.h> 
#include <LiquidCrystal_I2C.h> // LCD I2C를 사용하기 위한 라이브러리 추가
#define SOIL_HUMI A0 // 수분측정센서를 SOIL_HUMI라고 이름하고 A0핀에 연결
#define A_IA 6 // 모터드라이버 A_1A를 6번핀에 연결
#define A_IB 5 // 모터드라이버 A_1B를 5번핀에 연결을 설정

LiquidCrystal_I2C lcd(0x27, 16, 2); // LCD I2C 통신 설정(주소, 글자수, 줄수)

int soil, psoil;  // 수분센서 값을 저장하기 위해 soil, psoil이라는 변수를 선언, soil변수는 A0의 아날로그 값을 저장, psoil 변수는 map함수를 사용하여 soil값을 1~100으로 변환 값을 저장합니다. 
int red_pin =9; // LED 9번핀에 연결
int yellow_pin=10;
int blue_pin=11;
int piezo=12; // 피에조부저 12번핀에 연결

String msg=""; // String은 문자열 변수로 msg라는 변수에 “”의 문자를 직접 대입

void initMoter() {  // 모터를 초기화 하기 위한 함수 설정
 pinMode(A_IA, OUTPUT);
 pinMode(A_IB, OUTPUT);  
 analogWrite(A_IA, LOW);
 analogWrite(A_IB, LOW);
 pinMode(red_pin, OUTPUT); 
 pinMode(yellow_pin, OUTPUT);
 pinMode(blue_pin, OUTPUT);
 pinMode(piezo, OUTPUT); 
}

void initLcd() { // LCD를 초기화 하기 위한 함수 설정
 lcd.init(); 
 lcd.clear();
 lcd.backlight();
 lcd.setCursor(0,0);
}

void runMoter() { // 모터를 구동하기 위한 함수 설정
 digitalWrite(A_IA, HIGH);
 digitalWrite(A_IB, LOW);
 analogWrite(A_IA, 220); //화분의 크기에 따라 모터 세기를 적정하게 조절 0~255
}

void stopMoter() { // 모터를 정지하기 위한 함수 설정
 digitalWrite(A_IA, LOW);
 digitalWrite(A_IB, LOW);
}

void printLcd() {  // LCD를 표시하기 위한 함수 설정
 initLcd();
 lcd.print("Moisture: ");
 lcd.print(psoil);
 lcd.print("%");
 lcd.setCursor(0,1);
 lcd.print(msg);
}

void calcSoilHumidity() { // 수분습도센를 측정하여 모터를 구동하기 위한 함수 설정
 soil = analogRead(SOIL_HUMI); // A0에서 읽은 값을 soil 변수에 저장
 psoil = map(soil, 1000, 400, 5, 100); // 수분습도센는 습도가 높으면 저항값이 낮아 수치가 낮게 표시되고,    습도가 낮으면 수치가 높게 표시됨
 if(psoil < 20) { // psoil값이 20미만이면
msg = "Dry Soil : Pump"; // LCD에 메시지를 표시하고 
runMoter(); // 모터를 구동하고
digitalWrite(red_pin, HIGH); // 빨강 LED는 켜고
digitalWrite(yellow_pin, LOW);
digitalWrite(blue_pin, LOW);
digitalWrite(piezo, HIGH); // 피에조부저를 울려라.
delay(1000);
}

else if(psoil < 60) { // psoil값이 60미만이면
msg = "Humid soil"; // LCD에 메지지를 표시하고 
stopMoter(); // 모터는 정지하고
digitalWrite(red_pin, LOW);
digitalWrite(yellow_pin, HIGH); // 노랑 LED는 켜고
digitalWrite(blue_pin, LOW);
digitalWrite(piezo,LOW); // 피에조부저는 꺼라
}

else {   // psoil값이 이외의 수치이면 
msg = "in the water"; // LCD에 메지지를 표시하고
stopMoter(); // 모터는 정지하고
digitalWrite(red_pin, LOW); 
digitalWrite(yellow_pin, LOW);
digitalWrite(blue_pin, HIGH); // 노랑 LED는 켜고
digitalWrite(piezo,LOW); // 피에조부저는 꺼라
}
}

void setup() {  // 모터와 LCD를 초기화 하라
initMoter(); 
initLcd(); 
}

void loop() { // 토양습도나 여러 가지 주요 계산을 하고 제어문에 의해서 선택을 하는 함수를 실행한 뒤에 LCD에 그 값을 표시하는 것을 무한 반복하라
calcSoilHumidity(); 
printLcd(); 
}

