int echo =6;                     // echo핀 6번 사용
int trig =7;                     // trig핀 7번 사용

int cnt =0;

void setup() {
  pinMode(trig, OUTPUT);         // 초음파 센서 trig 출력 설정
  pinMode(echo, INPUT);          // 초음파 센서 echo 입력 설정
  pinMode(3, OUTPUT);            // 솔레노이드 3번 출력 설정
} 

void loop() {

  digitalWrite(trig, HIGH);
  delayMicroseconds(10);
  digitalWrite(trig, LOW);

  unsigned long duration = pulseIn(echo, HIGH);

  float distanceCM = ((34000 * duration) / 1000000) /2;  // 초음파 센서값 cm 단위로 변환

  if (distanceCM <=5)          // 초음파 센서 거리가 5cm 이하이면
  {
    if(cnt>=10)
    {
      digitalWrite(3,HIGH);       // 솔레노이드 작동
    }
    else cnt = cnt +2;
  }
  else                          // 그렇지 않으면(초음파 센서 거리가 5cm보다 크면)
  {
    if(cnt ==0)
    {
      digitalWrite(3,LOW);        // 솔레노이드 복귀
    }
    else cnt--;
  }
  delay(100);
}

