void loop() {
  bool rfid_detectTag = rfidReader.detectTag();
  static bool prev_rfid_detectTag = rfid_detectTag;
  static bool rfid_flag = false;


  if (rfid_detectTag || prev_rfid_detectTag) {
    rfid_flag = true;
  } else {
    rfid_flag = false;
  }


  if (getSwitchStatus(0) == PRESSED || getSwitchStatus(1) == PRESSED || rfid_flag == true) {
    /*
      0:  Switch 1
      1:  Switch 2
    */
    onRelay();
  } else {
    int direction = getDirection();
    switch (direction) {
      case CW:
        if (getPressure(0) > THRESHOLD || getPressure(1) > THRESHOLD) {
          /*
            0:  PRESSURE 1
            1:  PRESSURE 2
          */
          offRelay();
        }
        break;


      case CCW:
        if (getPressure(0) > THRESHOLD && getPressure(1) > THRESHOLD) {
          offRelay();
        }
        break;


      case MOVELESS:
      default:
        onRelay();
        break;
    }
  }


  prev_rfid_detectTag = rfid_detectTag;
  delay(50);
}
















