int getDirection() {
  int dir = 0;
  pulse_counter = encoder.read();
  if (old_pulse_counter == pulse_counter) {
    dir = MOVELESS;
  } else if (old_pulse_counter < pulse_counter) {
    dir = CCW;
  } else {
    dir = CW;
  }
  old_pulse_counter = pulse_counter;
  return dir;
}


int getPressure(int channel) {
  return analogRead(channel == 0 ? PRESSURE_1 : PRESSURE_2);
}


void offRelay(void) {
  digitalWrite(RELAY, LOW);
}


void onRelay(void) {
  digitalWrite(RELAY, HIGH);
}


int getSwitchStatus(int channel) {
  return (digitalRead(channel == 0 ? SW_1 : SW_2) == RELEASED) ? RELEASED : PRESSED;
}

