  pinMode(upbuttonPin, INPUT_PULLUP);           // 버튼핀 입력으로 설정, 내부 풀업 활성화
  pinMode(dnbuttonPin, INPUT_PULLUP);           // 버튼핀 입력으로 설정, 내부 풀업 활성화
  pinMode(opled, OUTPUT);
  pinMode(dnled, OUTPUT);
   }



void loop() 
{

   if(digitalRead(upbuttonPin)==LOW && digitalRead(dnbuttonPin)==HIGH) 
    {
     digitalWrite(opled, LOW);         // led 빨강 off
     digitalWrite(dnled, LOW);         // led 파랑 off
     digitalWrite(opled, HIGH);         // led 빨강 on
     delay(200);     
     digitalWrite(opled, LOW);         // led 빨강 off  
     delay(200);  
     digitalWrite(opled, HIGH);         // led 빨강 on
     }


