  // LED 위치가 1이 되면 마지막인 13으로 변경
  if (indexLed == 1) {
    indexLed = 13;
  }

  // 2번핀부터 13번핀까지 블링크 함수 호출
  for (int i = 5; i <= 13; i++) {
    blinkLed(i);
  }

  // LED 위치 감소
  indexLed--;
}

void blinkLed(int pinNo) {
  // 지정한 핀 ON
  digitalWrite(pinNo, HIGH);
  delay(delayTime);
  // LED 위치와 일치하지 않는 핀은 OFF
  if (pinNo != indexLed) {
    digitalWrite(pinNo, LOW);
  } else {
    delay(delayTime);
  }
}

